import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios'

import '../styles/Login.css'
const ResisterForm = () => {
    const [inputName,setName] = useState('');
    const [inputID,setID] = useState('');
    const [inputPW,setPW] = useState('');
    const [inputCheckPW,setCheckPW] = useState('');
    const [disable, setDisable] = useState(true);
    const navigate = useNavigate();

    const onChangeID = (e) =>{
        setID(e.target.value);
      }
      const onChangePW = (e) =>{
        setPW(e.target.value);
      }
      const onChangeCheckPW = (e) =>{
        setCheckPW(e.target.value);
      }
      const onChangeName = (e) =>{
        setName(e.target.value);
      }
    const checkInputValue = () => {
        if(inputName === "" || inputID ===  "" || inputPW === "" || inputCheckPW==="" ){
          setDisable(true);
        }
        else{
          setDisable(false);
        }
      }
      const clickToResister =async ()=>{
        if(inputPW === inputCheckPW){
            const data = {
                name: inputName,
                email : inputID,
                password : inputPW
                // date : todayTime()
            };
            // const res = await axios.post('http://localhost:8080/resister', data);

            // if(res.status === 200){
            //   alert("회원가입 성공 !!! ");
            // }else{
            //   alert("에러가 발생하였습니다.");
            // }

            // navigate("/LoginForm");
        }
        else{
            alert("비밀번호를 확인하시오.. ");
        }
      }
    useEffect(() => {
        checkInputValue();
      }, [inputName,inputID, inputPW,inputCheckPW])
    return (
        <div id="loginBody">
  <div className="container">
            <form className="form" id="createAccount">
                <h1 className="title">Create Account</h1>
                <div className="input_group">
                    <input type="text" id="signupUsername" className="input" autofocus placeholder="Username"  onChange={onChangeName}  value={inputName} />
                </div>
                <div id="msg" className="message message_error" />
                <div className="input_group">
                    <input type="text" className="input" autofocus placeholder="Email Address" onChange={onChangeID}  value={inputID}/>
                </div>
                <div className="input_group">
                    <input type="password" className="input" autofocus placeholder="Password"onChange={onChangePW}  value={inputPW} />
                </div>
                <div className="input_group">
                    <input type="password" className="input" autofocus placeholder="Confirm password" onChange={onChangeCheckPW}  value={inputCheckPW}/>
                </div>
                <button disabled={disable} onClick={clickToResister} className="btn" type="submit">Continue</button>
                <p className="text">
                    <a className="link" href="loginForm.html" id="linkLogin">Already have an account? Sign in</a>
                </p>
            </form>
        </div>
        </div>
    );
}
export default ResisterForm;
