import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios'

import '../styles/Login.css'

const LoginForm = () => {
     const navigate = useNavigate();
     const [inputID,setID] = useState('');
     const [inputPW,setPW] = useState('');
     const [disable, setDisable] = useState(true);

  const onChangeID = (e) =>{
    setID(e.target.value);
  }

  const onChangePW = (e) =>{
    setPW(e.target.value);
  }
  const checkInputValue = () => {
    if(inputID ===  "" || inputPW === "" ){
      setDisable(true);
    }
    else{
      setDisable(false);
    }
  }
  useEffect(() => {
    document.body.style.backgroundColor = "rgba(0,0,0,0.4)";
    checkInputValue();
  }, [])

    const clickToLogin = () => {
        
        document.body.style.backgroundColor = "rgba(0,0,0,0.0)";
         navigate("/message");
    };
    useEffect(() => {
        checkInputValue();
      }, [inputID, inputPW])
    return (
        <div id="loginBody">
            <div className="container">
                <form className="form" id="login">
                    <h1 className="title">Login</h1>
                    <div id="msg" className="message message_error" />
                    <div className="input_group">
                        <input type="text" id="loginUsername" className="input" autofocus placeholder="Username or email"  onChange={onChangeID}  value={inputID}/>
                    </div>
                    <div className="input_group">
                        <input type="password" id="loginUserpwd" className="input" autofocus placeholder="Password" onChange={onChangePW}  value={inputPW}/>
                    </div>
                    <button disabled={disable} onClick={clickToLogin} className="btn" type="submit" >Continue</button>
                    <p className="text">
                        <a href="#" className="link">Forgot your password?</a>
                    </p>
                    <p className="text">
                        <a href="signupForm.html" className="link" id="linkCreateAccount">Don't have an account? Create account</a>
                    </p>
                </form>
            </div>
        </div>
    );
}

export default LoginForm;
