import React, { useEffect } from "react";
import '../styles/Home.css'

const Home = () => {

    useEffect(() => {
        const requireValue = document.querySelectorAll('.frm_input full_input required');


const tabList = document.querySelectorAll('.tab_menu .list li');
for(var i = 0; i < tabList.length; i++){
  tabList[i].querySelector('.btn').addEventListener('click', function(e){
    e.preventDefault();
    for(var j = 0; j < tabList.length; j++){
      tabList[j].classList.remove('is_on');
    }
    this.parentNode.classList.add('is_on');
  });
}
function toast (_id, message, option){

let mother = document.getElementById(_id);
mother.removeChild(mother.firstChild);  //초기화
let doc = document.createElement('div');
mother.appendChild(doc);
doc.innerText = message;

//부모노드 입니다.
let motherStyle = {display: 'flex',position: 'fixed',bottom: '1rem','justify-content': 'center', width: '100%', 'z-index':99999999};
for(key in motherStyle) {  //부모테그에 스타일을 적용 합니다.
mother.style[key] = motherStyle[key];
}    

//자식노드 입니다.
let style = {opacity : 0, background : '#595959', color : 'white',  padding : '1rem 3.5rem 1rem 3.5rem', 'border-radius':'1rem'};
if(option) style = {...style, option};

//자식노드에 스타일 적용 합니다.
for(key in style) {
doc.style[key] = style[key];
}

let max = false;  //동작 완료여부 판별 변수 입니다.
let inter = setInterval(() => {  //이벤트
let number = parseFloat(doc.style.opacity)+0.02;
if(max) number = parseFloat(doc.style.opacity)-0.02;
if(number >= 1){
    setTimeout(() => {
        max = true;
        setTimeout(() => {
            mother.style.display = 'none';  //none처리를 통해서 다른 영역이 선택가능하게 합니다.
            clearInterval(inter);  //이벤트 끝!
        }, 3000);                    
    }, 500);
}
doc.style.opacity = number;
}, 10)

}

toast('toast', `안녕하세요. 방문해주셔서 감사합니다 !! `, {})

        const elm = document.querySelectorAll('.section');
        const elmCount = elm.length;
        elm.forEach(function (item, index) {
            item.addEventListener('mousewheel', function (event) {
                event.preventDefault();
                let delta = 0;

                if (!event) event = window.event;
                if (event.wheelDelta) {
                    delta = event.wheelDelta / 120;
                    if (window.opera) delta = -delta;
                }
                else if (event.detail)
                    delta = -event.detail / 3;

                let moveTop = window.scrollY;
                let elmSelector = elm[index];

                // wheel down : move to next section
                if (delta < 0) {
                    if (elmSelector !== elmCount - 1) {
                        try {
                            moveTop = window.pageYOffset + elmSelector.nextElementSibling.getBoundingClientRect().top;
                        } catch (e) { }
                    }
                }

                // wheel up : move to previous section
                else {
                    if (elmSelector !== 0) {
                        try {
                            moveTop = window.pageYOffset + elmSelector.previousElementSibling.getBoundingClientRect().top;
                        } catch (e) { }
                    }
                }

                const body = document.querySelector('html');
                window.scrollTo({ top: moveTop, left: 0, behavior: 'smooth' });
            });
        });

    }, [])

    const openNav = () => {
        //sidenav 클래스를 가진 div 요소 : width:0 숨기기
        document.getElementById("mySidenav").style.width = "250px";
        //css margin-left : 250px -> js로 고칠때는 marginLeft (-나오면 무조건 지우고 대문자)
        document.getElementById("main").style.marginLeft = "250px";
        document.body.style.backgroundColor = "rgba(0,0,0,0.4)";
    }
    const closeNav = () => {
        document.getElementById("mySidenav").style.width = "0px";
        document.getElementById("main").style.marginLeft = "0px";
        document.body.style.backgroundColor = "white";
    }

    return (
        <>
         <div id="toast"> </div>
            <header>
                <nav>
                    <span style={{ cursor: "pointer", fontSize: 30 }} onClick={openNav}>
                        ☰
                    </span>
                </nav>
            </header>
            <div id="mySidenav" className="sidenav">
                {/* <a href="javascript:closeNav()" class="closebtn" >&times;</a> */}
                <a href="javascript:void(0)" className="closebtn" onClick={closeNav}>
                    ×
                </a>
                <a className="moveHomeBtn">Home</a>
                <a className="moveProfileBtn">Profile</a>
                <a className="moveProjectsBtn">Projects</a>
                <a className="moveContactMeBtn">Contact Me</a>
            </div>
            {/* #main>h2>lorem3^p>lorem20^span */}
            <div id="main">
                <div className="section">
                    <div id="flexTop">
                        <h1>Home</h1>
                        <div id="right"> Portfolio by Ji Seung Do </div>
                    </div>
                    <div id="page1">
