import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios'
import '../styles/MessageBox.css'
// import MessageList from '../components/DataList';

const MessageBox = () => {
    const navigate = useNavigate();
    const [MessageData, setMessageData] = useState();
    const clickToBack = () => {
        navigate("/");
    };
    const getData = async () => {

        const res = await axios.get('http://localhost:8080/getList');
        setMessageData(res.data.getList);

    }

    useEffect(() => {
        getData();
    }, [])
    useEffect(() => {
        console.log(MessageData);
    }, [MessageData])
    return (

        <div id="messageBoxFont">

            <div className="board_wrap">
                <div className="board_title">
                    <strong>MessageBox</strong>
                </div>
                <div className="board_list_wrap">
                    <div className="board_list">
                        <div  id ="dsds">
                            <div className="num"><strong>제목</strong></div>
                            <div className="title"><strong>내용</strong></div>
                            <div className="writer"><strong>이름</strong></div>
                            <div className="date"><strong>이메일</strong></div>
                            <div className="count"><strong>삭제</strong></div>
                        </div>
                        <div>
                            {MessageData &&
                                MessageData.map((Message) => {
                                    return <div id ="dsds" key={Message.id}>
                                        {<div id ="mess" >
                                            <div className="num" > {Message.subject}</div>
                                            <div className="title">{Message.message}</div>
                                            <div className="writer"> {Message.name}</div>
                                            <div >{Message.email}</div>
                                            <div>삭제</div>

                                        </div>
                                        }
                                    </div>
                                })}
                        </div>
                        {/* <div>
                            <div className="num">4</div>
                            <div className="title"><a href="view.html">글 제목이 들어갑니다.</a></div>
                            <div className="writer">김이름</div>
                            <div className="date">2021.1.15</div>
                            <div className="count">33</div>
                        </div>
                        <div>
                            <div className="num">3</div>
                            <div className="title"><a href="view.html">글 제목이 들어갑니다.</a></div>
                            <div className="writer">김이름</div>
                            <div className="date">2021.1.15</div>
                            <div className="count">33</div>
                        </div>
                        <div>
                            <div className="num">2</div>
                            <div className="title"><a href="view.html">글 제목이 들어갑니다.</a></div>
                            <div className="writer">김이름</div>
                            <div className="date">2021.1.15</div>
                            <div className="count">33</div>
                        </div>
                        <div>
                            <div className="num">1</div>
                            <div className="title"><a href="view.html">글 제목이 들어갑니다.</a></div>
                            <div className="writer">김이름</div>
                            <div className="date">2021.1.15</div>
                            <div className="count">33</div>
                        </div> */}
                    </div>
                    {/* <div className="board_page">
                        <a href="#" className="bt first" />
                        <a href="#" className="bt prev" />
                        <a href="#" className="num on">1</a>
                        <a href="#" className="num">2</a>
                        <a href="#" className="num">3</a>
                        <a href="#" className="num">4</a>
                        <a href="#" className="num">5</a>
                        <a href="#" className="bt next">&gt;</a>
                        <a href="#" className="bt last">&gt;&gt;</a>
                    </div> */}
                    <div className="bt_wrap">
                        <a onClick={clickToBack} className="on">뒤로가기</a>
                        {/*<a href="#">수정</a>*/}
                    </div>
                </div>
            </div>
        </div>
    );
}
export default MessageBox;
